import React, { useState, useEffect } from "react";
import queryString from "query-string";
import io from "socket.io-client";

const Chat = ({ location }) => {
  // useEffect runs when the component renders
  // retrieve users data

  useEffect(() => {
    const data = queryString.parse(location.search);
    console.log(data);
  });

  return <h1>Chat</h1>;
};

export default Chat;
