//   <li>{number}</li>
// );

const Messages = ({ messages }) => (
  <div>
    <ScrollToBottom>
      {messages.map((message, i) => (
        <div>{message}</div>
      ))}
    </ScrollToBottom>
  </div>
);

export default Messages;
